import React, { useState } from 'react';
import './Login.css';
import { history } from '../History';
import Axios from 'axios';

export default function () {
  const [username, setusername] = useState("");
  const [password, setpassword] = useState("");
  const [Data,setdata] = useState({});
  const login = () => {
    if (username === "admin@ams" && password === "root") {
      history.push({
        pathname: `/admindashboard`,
      });
    }
    else {
      Axios.post("http://localhost:3001/login", {
        username: username,
        password: password,
        table: 1,
      }).then((response) => {console.log(response.data[0])
        setdata(response.data);
        console.log(Data)
      })
      console.log(Data.length)
      if (Data.length === 0) {
        Axios.post("http://localhost:3001/login", {
          username: username,
          password: password,
          table: 2,
        }).then((response) => {
          setdata(response.data);
        })
      }

    //   if (Data.length === 0) {
    //     alert("Wrong Credidentials");
    //   }
    //   else if (data[0].Class) {
    //     console.log(data[0])
    //     history.push({
    //       pathname: `/studentdashboard`,
    //     });
    //   }
    //   else {
    //     console.log(data[0])
    //     history.push({
    //       pathname: `/teacherdashboard`,
    //     });
    //   }
    }
};

return (
  <>
    <div className="form">
      <div className="imgcontainer">
      </div>
      <div className="container" >
        {/* <label htmlFor="uname"><b>Username</b></label> */}
        <input type="email" placeholder="Enter Username" name="uname" onChange={(e) => {
          setusername(e.target.value);
        }} />
        {/* <label htmlFor="psw"><b>Password</b></label> */}
        <input type="password" placeholder="Enter Password" name="psw" onChange={(e) => {
          setpassword(e.target.value);
        }} />
        <div className="submit">
          <button onClick={login}>Login</button>
        </div>

        {/* <label style={{color:'white'}}>
            <input type="checkbox" defaultChecked="checked" name="remember" /> Remember me
          </label> */}
        {/* <div className="container1" > */}
        {/* <button type="button" className="cancelbtn">Cancel</button> */}

        {/* </div> */}
      </div>
    </div>
    {/* <span className="psw"><a href="#">Forgot password?</a></span> */}
  </>
);
}
