import * as React from 'react';
import "./DashboardStyle.css";
import { Grid } from "@material-ui/core"
import { ExitToApp } from "@material-ui/icons"
import { history } from '../History';
import { useSelector } from 'react-redux';
import Axios from 'axios';
import { useEffect } from 'react';
import { Card,CardContent } from '@material-ui/core';
import { Typography } from '@material-ui/core';

const Assigned = ()=> {
    const student = useSelector((state) => (state.student[0]));
    const [assignedData, setAssignedData] = React.useState();
    const pathname = window.location.pathname;
    const Id = pathname.split('/')[1]; console.log(Id)

    const logout = () => {
        history.push({
            pathname: `/`,
        });
    };

    useEffect(() => {
        if (!student) {
            console.log('fetch')
            Axios.post("http://localhost:3001/assigned", {
                Student_id: Id
            }).then((response) => {
                console.log(response.data[0]);
                setAssignedData(response.data[0]);
            })
        }
        else {
            setAssignedData(student[0]);
        }
    }, []);

    const assigned =()=>{
        console.log("click")
        history.push({
            pathname: `/${Id}/assigned`,
        });
    }

    return (
        <Card >
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              title
            </Typography>
            <Typography variant="body2" >
              description
            </Typography>
          </CardContent>
          <CardContent  >
            {Array.isArray(batches)&&batches.map((value, index) => {
            //   const { startDate, endDate, status } = INTERNSHIP_BATCH_DETAILS;
            //   const batchSection = [[`${startDate}${value.startDate}`],
            //   [`${endDate}${value.endDate}`],
            //   [`${status}${value.status}`],
            //   ]
              return <BatchSection key={index}
                // details={batchSection
                batchNo={`Batch ${index + 1}`}
              />
            })}
          </CardContent>
        </Card>
    );
}

export default Assigned;
