import { Typography } from "@material-ui/core";
import React, { useEffect } from "react"
import './Login.css';


export default function () {
    useEffect()
    return (
        <div className="title">
            <h1 >Assignment Management System</h1>
            <h3 style={{ textAlign: 'center', color: 'white', width: '400px' }}>
                This project is a web-based project. The main objective of this project is to automate the assignments given to the students by the teacher. Teachers will be able to give assignments along with the deadline according to the subject code that they are teaching. Students will be able to submit the assignment on the UI itself. Teachers will be able to give marks and feedback according to each of the submission.
            </h3>
        </div>
    )
};
