import React, { useEffect, useState } from "react"
import './Login.css';


export default function () {
    const [quote,setquote]=useState("");
    useEffect(()=>{
        fetch("https://kutip.p.rapidapi.com/api/quote/latest?lang=en", {
        "method": "GET",
        "headers": {
            "x-rapidapi-key": "8a9e38958cmsh248e7056837eb7bp1e8ac9jsn63ade2a8d398",
            "x-rapidapi-host": "kutip.p.rapidapi.com"
        }
    })
        .then(response => {
            console.log(response);
            setquote(response);
        })
        .catch(err => {
            console.error(err);
        });
    }),[];
    return (
        <div className="title">
            <h1 >Assignment Management System</h1>
            <h3 style={{ textAlign: 'center', color: 'white', width: '400px' }}>
                {quote}
            </h3>
        </div>
    )
};
